import React, { useState } from "react";

function SignUp() {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState(""); // optional, backend can ignore
  const [password, setPassword] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch("http://localhost:5000/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password }),
        credentials: "include", // send cookies for session
      });
      const data = await res.json();
      alert(data.message);
    } catch (err) {
      console.error(err);
      alert("Registration failed");
    }
  };

  return (
    <div className="d-flex align-items-center justify-content-center vh-100 bg-light">
      <div className="card shadow-sm p-4" style={{ maxWidth: "400px", width: "100%" }}>
        <h1 className="h4 text-center mb-4 text-dark fw-bold">Sign Up</h1>

        <form onSubmit={handleSubmit}>
          {/* Username */}
          <div className="mb-3">
            <label className="form-label text-muted">Username</label>
            <input
              type="text"
              className="form-control"
              placeholder="Enter your username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </div>

          {/* Email (optional, not sent to backend currently) */}
          <div className="mb-3">
            <label className="form-label text-muted">Email</label>
            <input
              type="email"
              className="form-control"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>

          {/* Password */}
          <div className="mb-3">
            <label className="form-label text-muted">Password</label>
            <input
              type="password"
              className="form-control"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>

          {/* Submit button */}
          <button
            type="submit"
            className="btn btn-primary w-100"
            style={{ backgroundColor: "#387ed1", border: "none" }}
          >
            Create Account
          </button>
        </form>

        {/* Divider */}
        <div className="text-center my-3 text-muted">or</div>

        {/* Social login buttons */}
        <a
          href="http://localhost:5000/auth/google"
          className="btn btn-outline-danger w-100 mb-2 d-flex align-items-center justify-content-center"
        >
          <i className="bi bi-google me-2"></i> Continue with Google
        </a>
        <a
          href="http://localhost:5000/auth/github"
          className="btn btn-outline-dark w-100 d-flex align-items-center justify-content-center"
        >
          <i className="bi bi-github me-2"></i> Continue with GitHub
        </a>

        {/* Already have account */}
        <p className="text-center text-muted mt-4 mb-0">
          Already have an account?{" "}
          <a href="/login" className="text-primary text-decoration-none">
            Log In
          </a>
        </p>
      </div>
    </div>
  );
}

export default SignUp;
