import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch("http://localhost:5000/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password }),
        credentials: "include",
      });
      const data = await res.json();
      alert(data.message);
      if (res.ok) navigate("/"); // Redirect to homepage after login
    } catch (err) {
      console.error(err);
      alert("Login failed");
    }
  };

  return (
    <div className="d-flex align-items-center justify-content-center vh-100 bg-light">
      <div className="card shadow-sm p-4" style={{ maxWidth: "400px", width: "100%" }}>
        <h1 className="h4 text-center mb-4 text-dark fw-bold">Login</h1>
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label className="form-label text-muted">Username</label>
            <input
              type="text"
              className="form-control"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </div>
          <div className="mb-3">
            <label className="form-label text-muted">Password</label>
            <input
              type="password"
              className="form-control"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button
            type="submit"
            className="btn btn-primary w-100"
            style={{ backgroundColor: "#387ed1", border: "none" }}
          >
            Login
          </button>
        </form>
        <div className="text-center my-3 text-muted">or</div>
        <a href="http://localhost:5000/auth/google" className="btn btn-outline-danger w-100 mb-2">Continue with Google</a>
        <a href="http://localhost:5000/auth/github" className="btn btn-outline-dark w-100">Continue with GitHub</a>
        <p className="text-center text-muted mt-4 mb-0">
          Don't have an account? <a href="/signup" className="text-primary">Sign Up</a>
        </p>
      </div>
    </div>
  );
}

export default Login;
